export function getAllArticles(): Article[] {
  return articles.sort((a, b) => new Date(b.datePublished).getTime() - new Date(a.datePublished).getTime());
}

export function getArticleBySlug(slug: string): Article | undefined {
  return articles.find(article => article.slug === slug);
}

export function getFeaturedArticle(): Article | undefined {
  return articles.find(article => article.isFeatured);
}

export function getPopularArticles(limit: number = 3): Article[] {
  // For mock data, we'll just take some non-featured articles.
  // In a real app, this would be based on views, shares, etc.
  return articles.filter(article => !article.isFeatured).slice(0, limit);
}

export function getRecentArticles(limit: number = 4, excludeSlug?: string): Article[] {
  return articles
    .filter(article => article.slug !== excludeSlug)
    .sort((a, b) => new Date(b.datePublished).getTime() - new Date(a.datePublished).getTime())
    .slice(0, limit);
}

export function searchArticles(query: string): Article[] {
  if (!query) return getAllArticles(); // Or an empty array if preferred for no query
  const lowerCaseQuery = query.toLowerCase();
  return articles.filter(article =>
    article.title.toLowerCase().includes(lowerCaseQuery) ||
    article.excerpt.toLowerCase().includes(lowerCaseQuery) ||
    (article.tags && article.tags.some(tag => tag.toLowerCase().includes(lowerCaseQuery))) ||
    article.category.toLowerCase().includes(lowerCaseQuery)
  );
}

export function getArticlesByCategory(categoryName: string): Article[] {
  const lowerCaseCategoryName = categoryName.toLowerCase();
  return articles
    .filter(article => article.category.toLowerCase() === lowerCaseCategoryName)
    .sort((a, b) => new Date(b.datePublished).getTime() - new Date(a.datePublished).getTime());
}

export function getAllUniqueCategories(): string[] {
  const categories = articles.map(article => article.category.toLowerCase());
  return [...new Set(categories)];
}
